CREATE OR REPLACE PROCEDURE nut_liberar_todos_dia_pac (cd_setor_atendimento_p bigint, dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT distinct	x.nr_sequencia
	from	w_nutricao a,
		nut_atend_serv_dia c,
		nut_pac_opcao_rec x
	where	c.nr_atendimento 	= a.nr_atendimento
	and	x.nr_seq_servico_dia 	= c.nr_sequencia
	and	c.cd_setor_atendimento 	= cd_setor_atendimento_p
	and	coalesce(x.dt_liberacao::text, '') = ''
	and	c.dt_servico between inicio_dia(dt_referencia_p) and fim_dia(dt_referencia_p);




BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

		update 	nut_pac_opcao_rec
		set	dt_liberacao	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_w;
	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
