CREATE OR REPLACE PROCEDURE nut_liberar_todos_item_extra (nr_seq_serv_dia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w	bigint;
ie_receita_w	varchar(1);

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		CASE WHEN coalesce(nr_seq_receita::text, '') = '' THEN 'N'  ELSE 'S' END  
	from	nut_item_extra a 
	where	a.nr_seq_servico = nr_seq_serv_dia_p 
	and	coalesce(a.dt_liberacao::text, '') = '';


BEGIN 
 
if (nr_Seq_serv_dia_p IS NOT NULL AND nr_Seq_serv_dia_p::text <> '') then 
	open c01;
	loop 
	fetch c01 into nr_sequencia_w,ie_receita_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		 
		CALL nut_liberar_item_extra(nr_sequencia_w, ie_receita_w, nm_usuario_p);
		 
		end;
	end loop;
	close c01;
end if;	
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
