SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE nut_lib_serv_refeicao_html ( nr_seq_cardapios_p text, nm_usuario_p text) AS $body$
DECLARE


ds_cardapios_w		varchar(4000);
nr_cardapio_w		varchar(20);
nr_posicao_str_w 	bigint;


BEGIN

if (nr_seq_cardapios_p IS NOT NULL AND nr_seq_cardapios_p::text <> '') then

	ds_cardapios_w := nr_seq_cardapios_p;

	while( (ds_cardapios_w IS NOT NULL AND ds_cardapios_w::text <> '') or trim(both ds_cardapios_w) <> '' ) loop

		nr_posicao_str_w := position(', ' in ds_cardapios_w);

		if (nr_posicao_str_w = 0) then
			nr_cardapio_w := trim(both ds_cardapios_w);
			ds_cardapios_w := null;
		else
			nr_cardapio_w := substr(ds_cardapios_w, 1, nr_posicao_str_w - 1);
			ds_cardapios_w := substr(ds_cardapios_w, nr_posicao_str_w + 2);
		end if;

		update	nut_cardapio_dia
			set  dt_liberacao  = clock_timestamp(),
			nm_usuario_liberacao  = nm_usuario_p
		where 	nr_sequencia  = nr_cardapio_w;
		
		update	nut_pac_opcao_rec
			set dt_liberacao  = clock_timestamp()
		where 	nr_seq_cardapio_dia  = nr_cardapio_w;

	end loop;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
