CREATE OR REPLACE PROCEDURE nut_limpar_nut_pac_opcao_rec ( nr_seq_servico_dia_p bigint, ie_opcao_p text, cd_acompanhante_p text, nr_seq_acompanhante_p bigint, nr_seq_cardapio_dia_p bigint, nm_usuario_p text) AS $body$
BEGIN


if (nr_seq_servico_dia_p <> 0) then
	delete
	from 	nut_pac_opcao_rec
	where 	nr_seq_servico_dia = nr_seq_servico_dia_p
	and	coalesce(ie_receita_especial,'N') = ie_opcao_p
	AND	(((cd_acompanhante_p = 0) 	AND (coalesce(cd_acompanhante,0) = 0))     OR ((cd_acompanhante_p <> 0)     AND (coalesce(cd_acompanhante,0)     = cd_acompanhante_p)))
	AND	(((nr_seq_acompanhante_p = 0)   AND (coalesce(nr_seq_acompanhante,0) = 0)) OR ((nr_seq_acompanhante_p <> 0) AND (coalesce(nr_seq_acompanhante,0) = nr_seq_acompanhante_p)))
	and	coalesce(dt_liberacao::text, '') = '';
else
	delete
	from 	nut_pac_opcao_rec
	where 	nr_seq_cardapio_dia = nr_seq_cardapio_dia_p
	and	coalesce(ie_receita_especial,'N') = ie_opcao_p
	and	coalesce(dt_liberacao::text, '') = '';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
