SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE nut_obter_conversao_gramas ( nr_seq_genero_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w		integer;
cd_estabelecimento_w	smallint;
cd_unid_med_etq_w	varchar(30);
qt_conversao_w		double precision;


BEGIN

if (nr_seq_genero_p IS NOT NULL AND nr_seq_genero_p::text <> '') then

	select	max(cd_material),
		max(cd_estabelecimento)
	into STRICT	cd_material_w,
		cd_estabelecimento_w
	from	nut_genero_alim
	where	nr_sequencia = nr_seq_genero_p;

	if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then
		cd_unid_med_etq_w := substr(obter_dados_material_estab(cd_material_w,cd_estabelecimento_w,'UME'),1,30);
		qt_conversao_w := Obter_dose_convertida(cd_material_w,1,cd_unid_med_etq_w,'G');

		if (qt_conversao_w IS NOT NULL AND qt_conversao_w::text <> '') and (qt_conversao_w > 0) then
			update	nut_genero_alim
			set	qt_conversao = qt_conversao_w,
				dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p
			where	nr_sequencia = nr_seq_genero_p;
		else
			-- Não há regra para a unidade de medida no cadastro do material!
			CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(213861);
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
