CREATE OR REPLACE PROCEDURE nut_registrar_recol_refeicao (nr_sequencia_p bigint, nm_usuario_p text, dt_recolhimento_p timestamp, cd_copeira_p text) AS $body$
DECLARE


nm_usuario_copeira_w usuario.nm_usuario%type;


BEGIN

nm_usuario_copeira_w := nm_usuario_p;

if (cd_copeira_p IS NOT NULL AND cd_copeira_p::text <> '') then
	nm_usuario_copeira_w := obter_usuario_ativo_pf(cd_copeira_p);
end if;

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')then
	update 	nut_atend_serv_dia
	set	dt_recolhimento	= coalesce(dt_recolhimento_p,clock_timestamp()),
		nm_usuario_recolhimento = nm_usuario_copeira_w
	where	nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
