SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE nut_reg_prod_dietoterapica ( nr_locate_p text, ie_agrupador_p text default null) AS $body$
DECLARE


nr_sequencia_w		nut_producao_conduta_item.nr_sequencia%type;

C01 CURSOR FOR
SELECT	distinct a.nr_sequencia
from 	nut_producao_conduta_item a,
	nut_atend_serv_dia_dieta b
where	a.nr_seq_dieta = b.nr_sequencia
and	substr(nr_seq_prod_cond||CASE WHEN a.dt_producao='' THEN 'N'  ELSE 'S' END ||a.cd_material||b.cd_unidade_medida_dosa||fu_obter_itens_adic_prod(a.nr_sequencia,'NR'),1,250) =  nr_locate_p
and	((coalesce(ie_agrupador_p::text, '') = '') or (a.ie_agrupador = ie_agrupador_p) or (coalesce(a.ie_agrupador::text, '') = ''));


BEGIN

if (nr_locate_p IS NOT NULL AND nr_locate_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	nut_producao_conduta_item
		set	dt_producao		= clock_timestamp(),
			nm_usuario_producao	= wheb_usuario_pck.get_nm_usuario
		where	nr_sequencia		= nr_sequencia_w;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE nut_reg_prod_dietoterapica ( nr_locate_p text, ie_agrupador_p text default null) FROM PUBLIC;

