CREATE OR REPLACE PROCEDURE nut_suspender_itens_lactario ( nr_seq_item_lista_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_item_lista_w	varchar(2000);
nr_seq_item_w	bigint;


BEGIN
nr_seq_item_lista_w	:= nr_seq_item_lista_p;

while (nr_seq_item_lista_w IS NOT NULL AND nr_seq_item_lista_w::text <> '') loop
	begin
	nr_seq_item_w	:= substr(nr_seq_item_lista_w, 1, position(',' in nr_seq_item_lista_w) - 1);
	nr_seq_item_lista_w	:= substr(nr_seq_item_lista_w, position(',' in nr_seq_item_lista_w) + 1, length(nr_seq_item_lista_w));

	CALL nut_suspender_item_lactario(nr_seq_item_w, nm_usuario_p);
	end;
end loop;

end;
