CREATE OR REPLACE PROCEDURE nut_transferir_setor_servicos (nr_atendimento_p bigint, cd_setor_atendimento_p bigint, dt_transferencia_p timestamp, nm_usuario_p text) AS $body$
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') and (dt_transferencia_p IS NOT NULL AND dt_transferencia_p::text <> '') then

	update	nut_atend_serv_dia
	set	cd_setor_atendimento 	= cd_setor_atendimento_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_atendimento		= nr_atendimento_p
	and	dt_servico >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_transferencia_p);

end if;	
	
if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
