CREATE OR REPLACE PROCEDURE obter_alerta_nut ( cd_pessoa_fisica_p text, dt_dieta_p timestamp, ds_alerta_p INOUT text) AS $body$
DECLARE


ds_alerta_w	varchar(255);
quebra_w	varchar(10) := chr(13)||chr(10);

c01 CURSOR FOR
	SELECT	ds_alerta
	from	nut_alerta
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and (coalesce(dt_dieta_p,clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia,coalesce(dt_dieta_p,clock_timestamp()))) and fim_dia(coalesce(dt_fim_vigencia,coalesce(dt_dieta_p,clock_timestamp()))));


BEGIN

open c01;
loop
fetch c01 into ds_alerta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
		ds_alerta_p	:=	substr(ds_alerta_p||ds_alerta_w||quebra_w,1,255);
	end if;
end loop;
close c01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
