CREATE OR REPLACE PROCEDURE obter_ativacao_tabela_sistema ( nm_tabela_p text, nr_seq_visao_p bigint, ds_script_p INOUT text) AS $body$
DECLARE


cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w		integer := wheb_usuario_pck.get_cd_perfil;
cd_funcao_W		bigint := wheb_usuario_pck.get_cd_funcao;
nm_usuario_regra_w	varchar(15) := wheb_usuario_pck.get_nm_usuario;
	

BEGIN
	SELECT	max(ds_sql_ativacao)
	into STRICT	ds_script_p
	FROM	tabela_sistema_regra
	where 	nm_tabela = nm_tabela_p
	  and   coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_w,0))	= coalesce(cd_estabelecimento_w,0)
	  and 	coalesce(cd_perfil, coalesce(cd_perfil_w,0))	= coalesce(cd_perfil_w,0)
	  and 	coalesce(cd_funcao, coalesce(cd_funcao_w,0))	= coalesce(cd_funcao_w,0)
	  and 	coalesce(nr_seq_visao, coalesce(nr_seq_visao_p,0))  = coalesce(nr_seq_visao_p,0)
	  and 	coalesce(nm_usuario_regra, coalesce(nm_usuario_regra_w,' ')) = coalesce(nm_usuario_regra_w,' ')
	  and	(ds_sql_ativacao IS NOT NULL AND ds_sql_ativacao::text <> '');

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
