CREATE OR REPLACE PROCEDURE obter_checklists_pend_lib (nr_seq_check_proc_item_p bigint, nr_seq_atend_checklist_p bigint, ie_pendente_lib INOUT text, ds_itens_pendentes_p INOUT text) AS $body$
DECLARE

									 
nr_seq_item_dependencia_w 		bigint;
nm_item_checklist_w				varchar(255);
									
			 
c01 CURSOR FOR 
	SELECT	b.nr_seq_item_dependencia, 
			c.nm_item_checklist 
	FROM	checklist_processo_item a, 
			checklist_proc_item_depend b, 
			checklist_processo_item c 
	WHERE	a.nr_sequencia = b.nr_seq_check_proc_item 
	AND		c.nr_sequencia = b.nr_seq_item_dependencia 
	AND		a.nr_sequencia = nr_seq_check_proc_item_p 
	and		substr(obter_se_checklist_liberado(nr_seq_atend_checklist_p, c.nr_sequencia),1,10) = 'N';

									 

BEGIN 
ds_itens_pendentes_p 	:= '';
ie_pendente_lib			:= '';
 
 
if (nr_seq_check_proc_item_p IS NOT NULL AND nr_seq_check_proc_item_p::text <> '') then 
	 
	open c01;
	loop 
	fetch c01 into 	nr_seq_item_dependencia_w, 
					nm_item_checklist_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		ie_pendente_lib := 'S';
		if (coalesce(ds_itens_pendentes_p::text, '') = '') then 
			ds_itens_pendentes_p := nm_item_checklist_w;
		else 
			ds_itens_pendentes_p := substr((ds_itens_pendentes_p || ', ' || nm_item_checklist_w),1,255);
		end if;
		end;
	end loop;
	close c01;
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
