CREATE OR REPLACE PROCEDURE obter_classif_encaixe_agecons (cd_classificacao_p INOUT text) AS $body$
DECLARE


cd_classificacao_w	varchar(5);


BEGIN
select	coalesce(max(cd_classificacao),0)
into STRICT	cd_classificacao_w
from	agenda_classif
where	ie_tipo_classif	= 'O'
and	cd_classif_tasy	= 'E'
and	ie_situacao		= 'A';

if (cd_classificacao_w = '0') then
	select	CASE WHEN count(*)=0 THEN  'E'  ELSE 'E' || to_char(count(*)) END
	into STRICT	cd_classificacao_w
	from	agenda_paciente_classif;

	insert into agenda_classif(
					cd_classificacao,
					dt_atualizacao,
					nm_usuario,
					ds_classificacao,
					ie_tipo_classif,
					cd_classif_tasy,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ie_agenda,
					ie_situacao,
					ds_nome_curto
					)
	values (
					cd_classificacao_w,
					clock_timestamp(),
					'Tasy',
					'Encaixe',
					'O',
					'E',
					clock_timestamp(),
					'Tasy',
					'C',
					'A',
					'Encaixe'
					);
end if;

cd_classificacao_p := cd_classificacao_w;

commit;

end;
