CREATE OR REPLACE PROCEDURE obter_config_painel_quimio ( ds_cor_fonte_p INOUT text, ds_fonte_p INOUT text, qt_tamanho_p INOUT integer, ds_cor_fonte_grid_p INOUT text, ds_fonte_grid_p INOUT text, qt_tamanho_grid_p INOUT integer, ds_exibicao_p INOUT text, qt_tempo_atualizacao_p INOUT integer, qt_tempo_chamada_p INOUT integer, qt_tempo_alerta_p INOUT integer, nm_usuario_p text) AS $body$
BEGIN

select 	coalesce(max(ds_cor_fonte),'ClRed'), --cor da fonte no painel
	coalesce(max(ds_fonte),'Tahoma'), -- fonte do painel
	coalesce(max(qt_tamanho),0), -- tamanho da fonte o painel
	coalesce(max(ds_cor_fonte_grid),'clNavy'), -- cor da fonte no grid
	coalesce(max(ds_fonte_grid),'Tahoma'), -- fonte grid
	coalesce(max(qt_tamanho_grid),0), -- tamanho fonte grid
	coalesce(max(ie_modo_nome),'N'), -- exibicao paciente
	coalesce(max(qt_tempo_atualizacao),100), -- tempo (seg)
	coalesce(max(qt_tempo_chamada),0), -- tempo (minuto)
	coalesce(max(qt_tempo_alerta),0) -- tempo (minuto)
into STRICT	ds_cor_fonte_p,
	ds_fonte_p,
	qt_tamanho_p,
	ds_cor_fonte_grid_p,
	ds_fonte_grid_p,
	qt_tamanho_grid_p,
	ds_exibicao_p,
	qt_tempo_atualizacao_p,
	qt_tempo_chamada_p,
	qt_tempo_alerta_p
from	configuracao_painel_quimio;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
