CREATE OR REPLACE PROCEDURE obter_config_painel_swing ( nm_usuario_p text, qt_tamanho_fonte_p INOUT bigint, ds_fonte_p INOUT text, ie_negrito_p INOUT text, ie_italico_p INOUT text, ie_tracejado_p INOUT text, ds_cor_fonte_p INOUT text, ie_sublinhado_p INOUT text) AS $body$
DECLARE

ds_estilo_fonte_w varchar(100);

BEGIN
ie_negrito_p := 'N';
ie_italico_p := 'N';
ie_sublinhado_p := 'N';
ie_tracejado_p := 'N';
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	SELECT * FROM obter_config_painel(
			nm_usuario_p, ds_cor_fonte_p, ds_fonte_p, ds_estilo_fonte_w, qt_tamanho_fonte_p) INTO STRICT ds_cor_fonte_p, ds_fonte_p, ds_estilo_fonte_w, qt_tamanho_fonte_p;
	if (ds_estilo_fonte_w IS NOT NULL AND ds_estilo_fonte_w::text <> '') then
		begin
		if (position(ds_estilo_fonte_w in 'fsBold') > 0) then
			ie_negrito_p := 'S';
		end if;
		if (position(ds_estilo_fonte_w in 'fsItalic') > 0) then
			ie_italico_p := 'S';
		end if;
		if (position(ds_estilo_fonte_w in 'fsUnderline') > 0) then
			ie_sublinhado_p := 'S';
		end if;
		if (position(ds_estilo_fonte_w in 'fsStrikeOut') > 0) then
			ie_tracejado_p := 'S';
		end if;
		end;
	end if;
	end;
end if;
end;
