CREATE OR REPLACE PROCEDURE obter_config_resumo_oft ( nr_seq_item_p bigint, ds_cor_fonte_p INOUT text, ds_cor_fundo_p INOUT text, ds_fonte_p INOUT text, qt_tamanho_p INOUT integer, ds_titulo_p INOUT text, qt_tamanho_painel_p INOUT bigint, nm_usuario_p text) AS $body$
BEGIN

select 	coalesce(max(ds_cor_fonte),'ClRed'), --cor da fonte no memo
	coalesce(max(ds_cor_fundo),'ClWrite'), --cor de fundo do memo
	coalesce(max(ds_fonte),'Tahoma'), -- fonte do memo
	coalesce(max(qt_tamanho),0), -- tamanho da fonte o painel
	max(substr(obter_desc_item_oft(nr_seq_item),1,255)),
	coalesce(max(qt_tamanho_painel),255)
into STRICT	ds_cor_fonte_p,
	ds_cor_fundo_p,
	ds_fonte_p,
	qt_tamanho_p,
	ds_titulo_p,
	qt_tamanho_painel_p
from	configuracao_oft_resumo
where 	nr_seq_item = nr_seq_item_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
