CREATE OR REPLACE PROCEDURE obter_consistencias_quimio ( nr_seq_cabine_p bigint, cd_material_p text, nr_seq_lote_fornec_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_estoque_w	double precision;


BEGIN
ds_erro_p := '';


if (nr_seq_lote_fornec_p IS NOT NULL AND nr_seq_lote_fornec_p::text <> '') then
	select  coalesce(max(qt_estoque),0)
	into STRICT	qt_estoque_w
        from    far_estoque_cabine
        where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
        and	nr_seq_cabine = nr_seq_cabine_p
        and	cd_material = cd_material_p
        and	nr_seq_lote_fornec = nr_seq_lote_fornec_p;

	if (coalesce(qt_estoque_w,0) = 0) then
		ds_erro_p := wheb_mensagem_pck.get_texto(279808);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
