CREATE OR REPLACE PROCEDURE obter_conta_neg_boleto ( nr_seq_negociacao_p bigint, cd_estabelecimento_p bigint, nr_titulo_p bigint, nr_seq_conta_banco_p INOUT bigint) AS $body$
DECLARE


nr_seq_conta_banco_w	bigint;
dt_negociacao_w			timestamp;
cd_pessoa_fisica_w		negociacao_cr.cd_pessoa_fisica%type;
cd_cgc_w				negociacao_cr.cd_cgc%type;


C01 CURSOR FOR
SELECT	a.nr_seq_conta_banco
from	regra_negociacao_banco a
where	a.cd_estabelecimento	= cd_estabelecimento_p
and		dt_negociacao_w between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,dt_negociacao_w)
and		( (coalesce(a.ie_pf_pj,'A') = 'A') or
		( (coalesce(a.ie_pf_pj,'A') = 'PF') and (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') ) or
		( (coalesce(a.ie_pf_pj,'A') = 'PJ') and (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') ) );


BEGIN
select	max(a.dt_negociacao),
		max(a.cd_pessoa_fisica),
		max(a.cd_cgc)
into STRICT	dt_negociacao_w,
		cd_pessoa_fisica_w,
		cd_cgc_w
from	negociacao_cr a
where	a.nr_sequencia	= nr_seq_negociacao_p;

open C01;
loop
fetch C01 into
	nr_seq_conta_banco_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		nr_seq_conta_banco_p	:= nr_seq_conta_banco_w;
	end;
end loop;
close c01;

/* NÃ£o commitar */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
