SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_conta_pj ( cd_cgc_p text, cd_conta_contabil_p INOUT text) AS $body$
DECLARE


cd_empresa_w		bigint;
cd_conta_contabil_w	varchar(20)	:=	0;


BEGIN

select	coalesce(a.cd_conta_contabil,0)
into STRICT	cd_conta_contabil_w
from	pessoa_juridica a
where	a.cd_cgc						= cd_cgc_p;

if (cd_conta_contabil_w = 0) then
	--A pessoa júridica '||cd_cgc_p||' não possui conta contábil cadastrada'
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(211206,'CD_CGC=' ||cd_cgc_p);
end if;
cd_conta_contabil_p	:=  cd_conta_contabil_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
