CREATE OR REPLACE PROCEDURE obter_conteudo_email_js ( nr_seq_localizacao_p bigint, nr_ordem_servico_p bigint, ds_atualizacao_p INOUT text, ds_conteudo_p INOUT text, nm_usuario_p text) AS $body$
DECLARE

ie_webservice_w		varchar(1);
ds_atualizacao_w	varchar(4000);
nr_seq_hist_lib_w	bigint;
ds_conteudo_w		text;

BEGIN
select 	coalesce(max(ie_webservice), 'N')
into STRICT	ie_webservice_w
from 	man_localizacao
where 	nr_sequencia = nr_seq_localizacao_p;
if (ie_webservice_w = 'S') then
	ds_atualizacao_w := obter_texto_tasy(205159, 1);
end if;
select 	coalesce(max(x.nr_sequencia),0)
into STRICT	nr_seq_hist_lib_w
FROM man_tipo_hist c
LEFT OUTER JOIN man_ordem_serv_tecnico x ON (c.nr_sequencia = x.nr_seq_tipo)
WHERE x.nr_seq_ordem_serv = nr_ordem_servico_p  and coalesce(x.dt_liberacao,x.dt_atualizacao) = (
				SELECT 	max(coalesce(z.dt_liberacao,z.dt_atualizacao))
				from 	man_ordem_serv_tecnico z
				where 	z.nr_seq_ordem_serv = x.nr_seq_ordem_serv) and (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '') and coalesce(c.ie_classif_interno,'E') = 'E';
if (nr_seq_hist_lib_w > 0) then
		nr_seq_hist_lib_w := converte_rtf_html('select ds_relat_tecnico from man_ordem_serv_tecnico where nr_sequencia = :nr_sequencia', '', nm_usuario_p, nr_seq_hist_lib_w);
	if (nr_seq_hist_lib_w > 0) then
		select 	ds_texto
		into STRICT	ds_conteudo_w
		from 	tasy_conversao_rtf
		where 	nr_sequencia = nr_seq_hist_lib_w;
	end if;
end if;

ds_atualizacao_p	:= ds_atualizacao_w;
ds_conteudo_p		:= ds_conteudo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
