CREATE OR REPLACE PROCEDURE obter_convenio_execucao_java ( nr_atendimento_p bigint, dt_execucao_p text, cd_convenio_p INOUT bigint, cd_categoria_p INOUT text, nr_doc_convenio_p INOUT text, ie_tipo_guia_p INOUT text, cd_senha_p INOUT text) AS $body$
DECLARE

					 
/*Conforme OS 666747, foi descrito pelo gerente da tecologia que é necessário criar uma procedure temporária para ser chamada no java, passando o parâmetro dt_execucao_p 
como varchar2 pois na procedure obter_convenio_execucao passa como date, esta causava problemas com o horário de versão, sendo que foi criada ésta que converte o parâmetro 
para date e apenas chama a procedure original, ésta foi uma solução paliativa encontrada para resolver o problema do cliente, está sendo estudada uma solução definitiva pela tecnologia, 
pois o problema ocorre na comunicação entre o tomcat e o java*/
 
 
dt_execucao_w	timestamp;


BEGIN 
 
dt_execucao_w := to_date(dt_execucao_p,'dd/mm/yyyy hh24:mi:ss');
 
SELECT * FROM obter_convenio_execucao(nr_atendimento_p, dt_execucao_w, cd_convenio_p, cd_categoria_p, nr_doc_convenio_p, ie_tipo_guia_p, cd_senha_p) INTO STRICT cd_convenio_p, cd_categoria_p, nr_doc_convenio_p, ie_tipo_guia_p, cd_senha_p;
 
end;
$body$
