CREATE OR REPLACE PROCEDURE obter_conversao_integracao (nm_tabela_p text, cd_origem_p text, cd_origem_secund_p text, ds_integracao_p text, cd_tasy_p INOUT text, ie_inclui_p INOUT text) AS $body$
DECLARE


cd_tasy_w		varchar(20);
nr_sequencia_w		bigint;
cd_origem_secund_w	varchar(20);


BEGIN

cd_origem_secund_w	:= coalesce(cd_origem_secund_p, '0');
ie_inclui_p		:= 'N'; /* Campo para dizer se devemos fazer insert na tabela da integração */
select	max(cd_tasy)
into STRICT	cd_tasy_w
from	INTEGRACAO_TABELA
where	nm_tabela		= nm_tabela_p
and	ds_integracao		= ds_integracao_p
and	cd_origem		= cd_origem_p;

if (coalesce(cd_tasy_w::text, '') = '') then
	begin

	select	nextval('integracao_tabela_seq')
	into STRICT	nr_sequencia_w
	;

	if (nm_tabela_p	= 'PESSOA_FISICA') then
		select	nextval('pessoa_fisica_seq')
		into STRICT	cd_tasy_w
		;
	elsif (nm_tabela_p	= 'SETOR_ATENDIMENTO') then
		select	coalesce(max(cd_setor_atendimento), 0) + 1
		into STRICT	cd_tasy_w
		from	SETOR_atendimento;
	elsif (nm_tabela_p	= 'CENTRO_CUSTO') then
		select	coalesce(max(cd_centro_custo), 0) + 1
		into STRICT	cd_tasy_w
		from	centro_custo;
	elsif (nm_tabela_p	= 'LOCAL_ESTOQUE') then
		select	coalesce(max(cd_local_estoque), 0) + 1
		into STRICT	cd_tasy_w
		from	local_estoque;
	elsif (nm_tabela_p	= 'TIPO_ACOMODACAO') then
		select	coalesce(max(cd_tipo_acomodacao), 0) + 1
		into STRICT	cd_tasy_w
		from	tipo_acomodacao;
	elsif (nm_tabela_p	= 'CONVENIO') then
		select	coalesce(max(cd_convenio), 0) + 1
		into STRICT	cd_tasy_w
		from	convenio;
	elsif (nm_tabela_p	= 'CONVENIO') then
		select	coalesce(max(cd_convenio), 0) + 1
		into STRICT	cd_tasy_w
		from	convenio;
	end if;

	insert	into INTEGRACAO_TABELA(nr_sequencia,
		nm_tabela,
		dt_atualizacao,
		nm_usuario,
		cd_tasy,
		ds_integracao,
		cd_origem)
	values (nr_sequencia_w,
		nm_tabela_p,
		clock_timestamp(),
		'TASY',
		cd_tasy_w,
		ds_integracao_p,
		cd_origem_p);

	ie_inclui_p	:= 'S';

	end;
end if;

cd_tasy_p	:= cd_tasy_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
