CREATE OR REPLACE PROCEDURE obter_conv_cat_cadastro ( cd_pessoa_fisica_p text, cd_categoria_p INOUT text, cd_convenio_p INOUT bigint, cd_carteirinha_P INOUT text) AS $body$
DECLARE


nr_sequencia_w	bigint;

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	pessoa_titular_convenio
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (nr_sequencia_w > 0) then

		select	max(cd_convenio),
			max(cd_categoria),
			max(substr(cd_usuario_convenio,1,30))
		into STRICT	cd_convenio_p,
			cd_categoria_p,
			cd_carteirinha_p
		from	pessoa_titular_convenio
		where 	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	nr_sequencia = nr_sequencia_w;
	end if;
end if;

commit;

end;
