CREATE OR REPLACE PROCEDURE obter_conv_ds_reconstituir_md (cd_unid_med_dose_mat_p text, qt_conv_ml_p INOUT bigint, qt_dose_unid_med_cons_p INOUT bigint, qt_dose_mat_p bigint, ds_conv_ml_p INOUT text, ds_reconstituir_p INOUT text, cd_unidade_medida_p text, qt_dose_str_p text, cd_unid_med_dose_p text, ds_material_p text, ds_unid_med_ml_p text, qt_conv_unid_p bigint) AS $body$
