SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_conv_todas_agendas (cd_pessoa_fisica_p text, cd_tipo_agenda_p bigint, nr_seq_agenda_p bigint, ie_forma_convenio_p text, cd_convenio_p INOUT bigint, cd_categoria_p INOUT text, cd_usuario_convenio_p INOUT text, dt_validade_carteira_p INOUT timestamp, nr_doc_convenio_p INOUT text, cd_plano_p INOUT text, cd_tipo_acomodacao_p INOUT bigint, ds_observacao_p INOUT text) AS $body$
DECLARE


dt_agenda_w			timestamp;
nr_seq_agenda_cons_w	agenda_consulta.nr_sequencia%type;
nr_seq_agenda_pac_w		agenda_paciente.nr_sequencia%type;
cd_convenio_w			integer;
cd_categoria_w			varchar(10);
cd_usuario_convenio_w		varchar(30);
dt_validade_w			timestamp;
nr_doc_convenio_w		varchar(20);
cd_tipo_acomodacao_w		smallint;
cd_plano_w			varchar(10);
ds_observacao_w			varchar(4000);
ie_tipo_agenda_w		varchar(2); -- AGENDA_CONSULTA OU AGENDA_PACIENTE
dt_agenda_agecons_w		timestamp;
dt_agenda_agepac_w		timestamp;
cd_agenda_cons_w		agenda_consulta.cd_agenda%type;


BEGIN


if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	if (cd_tipo_agenda_p in (3,4,5)) then
		
		select  max(dt_agenda)
		into STRICT	dt_agenda_w
		from	agenda_consulta
		where	nr_sequencia = nr_seq_agenda_p;
	end if;
		
	if (cd_tipo_agenda_p in (1,2)) then
		
		select  max(hr_inicio)
		into STRICT	dt_agenda_w
		from	agenda_paciente
		where	nr_sequencia = nr_seq_agenda_p;
	end if;
	
	select 	max(dt_agenda),
			max(nr_sequencia)			
	into STRICT	dt_agenda_agecons_w,
			nr_seq_agenda_cons_w			
	from	agenda_consulta
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and		dt_agenda < dt_agenda_w
	and		ie_status_agenda not in ('C', 'F', 'I', 'II', 'B', 'L');
	
	
	
	if (coalesce(dt_agenda_agecons_w::text, '') = '') then
		dt_agenda_agecons_w :=  to_date('01/01/1899 00:00:01', 'dd/mm/yyyy hh24:mi:ss');
	end if;
	
	select  max(hr_inicio),
			max(nr_sequencia)
	into STRICT	dt_agenda_agepac_w,
			nr_seq_agenda_pac_w
	from	agenda_paciente
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and		hr_inicio < dt_agenda_w
	and		ie_status_agenda not in ('C', 'F', 'I', 'II', 'B', 'L');
	
	if (coalesce(dt_agenda_agepac_w::text, '') = '') then
		dt_agenda_agepac_w :=  to_date('01/01/1899 00:00:01', 'dd/mm/yyyy hh24:mi:ss');
	end if;
	
	if (dt_agenda_agecons_w > dt_agenda_agepac_w) then
		ie_tipo_agenda_w	:= 'AC';
	elsif (dt_agenda_agecons_w < dt_agenda_agepac_w) then
		ie_tipo_agenda_w	:= 'AP';
	end if;
	
	/*insert into log_agenda (	
		ds_dados)
	values(	ie_tipo_agenda_w || ' - ' || dt_agenda_agepac_w || ' - ' || cd_agenda_cons_w); 
	commit;*/
	
	if (ie_tipo_agenda_w = 'AP') then
			/* obter convenio 'Agenda_paciente' */

			if (nr_seq_agenda_pac_w > 0) then
				select	max(a.cd_convenio),
					max(a.cd_categoria),
					max(a.cd_usuario_convenio),
					max(a.dt_validade_carteira),
					max(a.nr_doc_convenio),
					max(a.cd_tipo_acomodacao),
					max(a.cd_plano),
					max(a.ds_observacao)
				into STRICT	cd_convenio_w,
					cd_categoria_w,
					cd_usuario_convenio_w,
					dt_validade_w,
					nr_doc_convenio_w,
					cd_tipo_acomodacao_w,
					cd_plano_w,
					ds_observacao_w
				from	agenda_paciente a,
					agenda b
				where	nr_sequencia = nr_seq_agenda_pac_w
				and	a.cd_agenda = b.cd_agenda;
			end if;	

		cd_convenio_p		:= cd_convenio_w;
		cd_categoria_p		:= cd_categoria_w;
		cd_usuario_convenio_p	:= cd_usuario_convenio_w;
		dt_validade_carteira_p	:= dt_validade_w;
		nr_doc_convenio_p	:= nr_doc_convenio_w;
		cd_tipo_acomodacao_p	:= cd_tipo_acomodacao_w;
		cd_plano_p		:= cd_plano_w;
		ds_observacao_p		:= ds_observacao_w;
	end if;
	
	if (ie_tipo_agenda_w = 'AC') then	
			/* obter convenio  'Agenda_consulta'*/

			if (nr_seq_agenda_cons_w > 0) then
				select	max(a.cd_convenio),
					max(a.cd_categoria),
					max(a.cd_usuario_convenio),
					max(a.dt_validade_carteira),
					max(a.nr_doc_convenio),
					max(a.cd_tipo_acomodacao),
					max(a.cd_plano)
				into STRICT	cd_convenio_w,
					cd_categoria_w,
					cd_usuario_convenio_w,
					dt_validade_w,
					nr_doc_convenio_w,
					cd_tipo_acomodacao_w,
					cd_plano_w
				from	agenda_consulta a,
					agenda b
				where	nr_sequencia = nr_seq_agenda_cons_w
				and	a.cd_agenda = b.cd_agenda;

			end if;	

		cd_convenio_p		:= cd_convenio_w;
		cd_categoria_p		:= cd_categoria_w;
		cd_usuario_convenio_p	:= cd_usuario_convenio_w;
		dt_validade_carteira_p	:= dt_validade_w;
		nr_doc_convenio_p	:= nr_doc_convenio_w;
		cd_tipo_acomodacao_p	:= cd_tipo_acomodacao_w;
		cd_plano_p		:= cd_plano_w;
	end if;
end if;	
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_conv_todas_agendas (cd_pessoa_fisica_p text, cd_tipo_agenda_p bigint, nr_seq_agenda_p bigint, ie_forma_convenio_p text, cd_convenio_p INOUT bigint, cd_categoria_p INOUT text, cd_usuario_convenio_p INOUT text, dt_validade_carteira_p INOUT timestamp, nr_doc_convenio_p INOUT text, cd_plano_p INOUT text, cd_tipo_acomodacao_p INOUT bigint, ds_observacao_p INOUT text) FROM PUBLIC;

