CREATE OR REPLACE PROCEDURE obter_custo_medio_itens_nf (cd_estabelecimento_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


nr_item_nf_w		integer;
cd_material_w		integer;
qt_material_w		double precision;
vl_custo_medio_w	double precision;
vl_desconto_w		double precision;
vl_total_item_nf_w	double precision;


c01 CURSOR FOR
SELECT	nr_item_nf,
	cd_material,
	qt_item_nf,
	vl_desconto
from 	nota_fiscal_item
where	nr_sequencia = nr_sequencia_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_item_nf_w,
	cd_material_w,
	qt_material_w,
	vl_desconto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
/*	select	nvl(max(obter_custo_medio_material(cd_estabelecimento_p,sysdate,cd_material_w)),0)
	into	vl_custo_medio_w
	from 	dual;*/
	vl_custo_medio_w := obter_custo_medio_mat(cd_estabelecimento_p, trunc(clock_timestamp(),'mm'), cd_material_w, vl_custo_medio_w);


	vl_total_item_nf_w	:=	(vl_custo_medio_w * qt_material_w);

	update	nota_fiscal_item
	set	vl_unitario_item_nf	=	vl_total_item_nf_w / qt_material_w,
		vl_liquido		=	vl_total_item_nf_w - vl_desconto_w,
		vl_total_item_nf	=	vl_total_item_nf_w
	where	nr_item_nf		=	nr_item_nf_w
	and	nr_sequencia		=	nr_sequencia_p;
	commit;
	end;
end loop;
close c01;


end;
$body$
