CREATE OR REPLACE PROCEDURE obter_dados_agendar_periodo ( nr_seq_agenda_p bigint, ie_diario_p text, ie_final_semana_p text, qt_intervalo_p bigint, ds_dias_p text, dt_termino_p INOUT timestamp, ds_erro_p INOUT text, ie_next_day_p text default 'N') AS $body$
DECLARE


ie_possui_sessoes_w	varchar(1);


BEGIN

select	CASE WHEN coalesce(qt_total_secao,0)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_possui_sessoes_w
from	agenda_consulta
where	nr_sequencia = nr_seq_agenda_p;

if (ie_possui_sessoes_w = 'N') then
	ds_erro_p	:= substr(obter_texto_tasy(51121, wheb_usuario_pck.get_nr_seq_idioma),1,255);

elsif (ie_diario_p = 'S') then
	begin
	select	obter_dados_secao_per_agenda_d(nr_seq_agenda_p, ie_diario_p, ie_final_semana_p, 'D', ds_dias_p)
	into STRICT	dt_termino_p
	;
	end;
else
	begin
	select	obter_dados_secao_per_agenda(nr_seq_agenda_p, ie_diario_p, ie_final_semana_p, qt_intervalo_p, 'D', ie_next_day_p)
	into STRICT	dt_termino_p
	;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
