CREATE OR REPLACE PROCEDURE obter_dados_agenda_paciente ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, ie_executa_consulta_p text, dt_nascimento_pac_p INOUT timestamp, qt_idade_paciente_p INOUT bigint, qt_idade_mes_p INOUT bigint, cd_pessoa_fisica_out_p INOUT text, nm_paciente_p INOUT text, nr_telefone_p INOUT text) AS $body$
DECLARE


cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN
if (ie_executa_consulta_p = 'S') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin
	cd_pessoa_fisica_w := obter_pessoa_atendimento(nr_atendimento_p, 'C');
	end;
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	cd_pessoa_fisica_w := cd_pessoa_fisica_p;
	end;
end if;
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	begin
	select	substr(obter_nome_pf(cd_pessoa_fisica), 1, 255)
	into STRICT	nm_paciente_p
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;
	
	qt_idade_paciente_p	:= obter_dados_pf(cd_pessoa_fisica_w, 'I');
	dt_nascimento_pac_p	:= to_date(substr(obter_dados_pf(cd_pessoa_fisica_w, 'DN'), 1, 10), 'DD/MM/YYYY');
	qt_idade_mes_p		:= obter_idade(obter_data_nascto_pf(cd_pessoa_fisica_w),clock_timestamp(),'MM');
	nr_telefone_p		:= obter_fone_pac_agenda(cd_pessoa_fisica_w);
	cd_pessoa_fisica_out_p	:= cd_pessoa_fisica_w;
	end;
end if;
end;
$body$
