CREATE OR REPLACE PROCEDURE obter_dados_antibiograma ( nr_seq_resultado_p bigint, nr_seq_result_item_p bigint, nr_prescricao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nm_exame_p INOUT text, nr_seq_material_p INOUT bigint, cd_setor_entrega_p INOUT bigint, ie_forma_mostrar_ant_p INOUT text) AS $body$
