CREATE OR REPLACE PROCEDURE obter_dados_antimicrob ( cd_material_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_justificativa_p INOUT text, ie_objetivo_p INOUT text, ie_dia_utilizacao_p INOUT text, ie_topografia_p INOUT text, ie_amostra_p INOUT text, ie_microorganismo_p INOUT text, ie_origem_p INOUT text, ie_avaliacao_p INOUT text, ie_exige_cid_p INOUT text, ie_exige_indicacao_p INOUT text, cd_intervalo_p text default null, qt_dias_prev_p INOUT bigint DEFAULT NULL, ie_objetivo_filtro_p text DEFAULT NULL, cd_setor_atendimento_p bigint DEFAULT NULL) AS $body$
DECLARE


ie_justificativa_antimicrob_w	char(1);
ie_objetivo_antimicrob_w		char(1);
ie_dia_utilizacao_antimicrob_w	char(1);
ie_topografia_antimicrob_w		char(1);
ie_amostra_antimicrob_w			char(1);
ie_microorganismo_antimicrob_w	char(1);
ie_origem_antimicrob_w			char(1);
ie_avaliacao_antimicrob_w		char(1);
ie_exige_cid_antimicrob_w		char(1);
qt_dias_prev_w					regra_antimicrobiano.qt_dias_prev%type;
cd_grupo_material_w				grupo_material.cd_grupo_material%type;
cd_subgrupo_w					subgrupo_material.cd_subgrupo_material%type;
nr_seq_familia_w				material_familia.nr_sequencia%type;
cd_classe_material_w			classe_material.cd_classe_material%type;
ie_ctrl_medico_antimicrob_w		char(1);
ie_indicacao_clinica_w			char(1);
ie_dia_semana_w					smallint;
cd_material_estoque_w			material.cd_material%type;
nr_seq_ficha_tecnica_w			material.nr_seq_ficha_tecnica%type;
ie_existe_w						char(1);
ie_feriado_w 					bigint;
ie_controle_medico_w			material.ie_controle_medico%type;

c01 CURSOR FOR
SELECT	ie_justificativa,
		ie_objetivo,
		ie_dia_utilizacao,
		ie_topografia,
		ie_amostra,
		ie_microorganismo,
		ie_origem,
		ie_avaliacao,
		ie_exige_cid,
		coalesce(ie_indicacao,'N'),
		qt_dias_prev
from	regra_antimicrobiano
where	cd_regra				= ie_ctrl_medico_antimicrob_w
and		cd_estabelecimento		= cd_estabelecimento_p
and		((coalesce(cd_perfil::text, '') = '') or (cd_perfil = cd_perfil_p))
and		((coalesce(cd_intervalo::text, '') = '') or (cd_intervalo = cd_intervalo_p))
and		coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w
and		coalesce(cd_subgrupo_material,cd_subgrupo_w) = cd_subgrupo_w
and		coalesce(nr_seq_familia,nr_seq_familia_w) = nr_seq_familia_w
and		coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w
and		coalesce(cd_material,cd_material_p) = cd_material_p
and		coalesce(cd_material_estoque, cd_material_estoque_w) = cd_material_estoque_w
and		coalesce(nr_seq_ficha_tecnica, nr_seq_ficha_tecnica_w) = nr_seq_ficha_tecnica_w
and		coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p, 0)) = coalesce(cd_setor_atendimento_p, 0)
and		((ie_objetivo_filtro = ie_objetivo_filtro_p) or (coalesce(ie_objetivo_filtro::text, '') = ''))
and		((coalesce(dt_hora_inicio::text, '') = '') or (	pkg_date_utils.get_DateTime(clock_timestamp(), coalesce(dt_hora_inicio, PKG_DATE_UTILS.GET_TIME('00:00:00'))) < clock_timestamp()))
and		((coalesce(dt_hora_fim::text, '') = '') or (	pkg_date_utils.get_DateTime(clock_timestamp(), coalesce(dt_hora_fim, PKG_DATE_UTILS.GET_TIME('00:00:00'))) > clock_timestamp()))
and		(((ie_dia_semana = 9) or (coalesce(ie_dia_semana,ie_dia_semana_w) = ie_dia_semana_w))
		or (ie_feriado_w > 0 AND ie_dia_semana = 0))
order by
		coalesce(cd_material,0),
		coalesce(cd_material_estoque,0),
		coalesce(nr_seq_ficha_tecnica,0),
		coalesce(nr_seq_familia,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0),
		coalesce(cd_perfil,0),
		coalesce(ie_dia_semana,0),
		coalesce(to_char(dt_hora_inicio,'hh24:mi:ss'),'00:00:00'),
		coalesce(to_char(dt_hora_fim,'hh24:mi:ss'),'00:00:00'),
		coalesce(ie_objetivo_filtro,'X') desc,
		coalesce(cd_setor_atendimento,0);


BEGIN
if (coalesce(cd_material_p,0) = 0) then
	return;
end if;

select	coalesce(max('S'),'N')
into STRICT	ie_existe_w
from	regra_antimicrobiano LIMIT 1;

if (ie_existe_w = 'S') then

	ie_feriado_w := coalesce(obter_se_feriado(cd_estabelecimento_p, clock_timestamp()),0);

	ie_dia_semana_w	:= pkg_date_utils.get_weekday(clock_timestamp());

	select	max(ie_controle_medico)
	into STRICT	ie_controle_medico_w
	from 	material
	where 	cd_material = cd_material_p;

	ie_ctrl_medico_antimicrob_w	:= substr(obter_controle_medic(cd_material_p,cd_estabelecimento_p,ie_controle_medico_w,null,cd_setor_atendimento_p,null),1,1);

	select	coalesce(max(cd_grupo_material),0),
			coalesce(max(cd_subgrupo_material),0),
			coalesce(max(cd_classe_material),0),
			coalesce(max(nr_seq_familia),0),
			coalesce(max(cd_material_estoque),0),
			coalesce(max(nr_seq_ficha_tecnica),0)
	into STRICT	cd_grupo_material_w,
			cd_subgrupo_w,
			cd_classe_material_w,
			nr_seq_familia_w,
			cd_material_estoque_w,
			nr_seq_ficha_tecnica_w
	from	estrutura_material_v
	where 	cd_material	= cd_material_p;

	open c01;
	loop
	fetch c01 into
			ie_justificativa_antimicrob_w,
			ie_objetivo_antimicrob_w,
			ie_dia_utilizacao_antimicrob_w,
			ie_topografia_antimicrob_w,
			ie_amostra_antimicrob_w,
			ie_microorganismo_antimicrob_w,
			ie_origem_antimicrob_w,
			ie_avaliacao_antimicrob_w,
			ie_exige_cid_antimicrob_w,
			ie_indicacao_clinica_w,
			qt_dias_prev_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ie_justificativa_p 		:= ie_justificativa_antimicrob_w;
		ie_objetivo_p 			:= ie_objetivo_antimicrob_w;
		ie_dia_utilizacao_p 	:= ie_dia_utilizacao_antimicrob_w;
		ie_topografia_p			:= ie_topografia_antimicrob_w;
		ie_amostra_p 			:= ie_amostra_antimicrob_w;
		ie_microorganismo_p 	:= ie_microorganismo_antimicrob_w;
		ie_origem_p 			:= ie_origem_antimicrob_w;
		ie_avaliacao_p 			:= ie_avaliacao_antimicrob_w;
		ie_exige_cid_p 			:= ie_exige_cid_antimicrob_w;
		ie_exige_indicacao_p	:= ie_indicacao_clinica_w;
		qt_dias_prev_p			:= qt_dias_prev_w;
		end;
	end loop;
	close c01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_dados_antimicrob ( cd_material_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_justificativa_p INOUT text, ie_objetivo_p INOUT text, ie_dia_utilizacao_p INOUT text, ie_topografia_p INOUT text, ie_amostra_p INOUT text, ie_microorganismo_p INOUT text, ie_origem_p INOUT text, ie_avaliacao_p INOUT text, ie_exige_cid_p INOUT text, ie_exige_indicacao_p INOUT text, cd_intervalo_p text default null, qt_dias_prev_p INOUT bigint DEFAULT NULL, ie_objetivo_filtro_p text DEFAULT NULL, cd_setor_atendimento_p bigint DEFAULT NULL) FROM PUBLIC;

