CREATE OR REPLACE PROCEDURE obter_dados_atendimento_quimio ( nr_seq_paciente_p bigint, nr_atendimento_p bigint, ds_estagio_autor_p text, nm_usuario_p text, cd_estabelecimento_p bigint, cd_pessoa_fisica_p INOUT text, cd_medico_resp_p INOUT text, ds_erro_p INOUT text) AS $body$
DECLARE


ie_permite_atend_autorizado_w	varchar(1);


BEGIN

/* Quimioterapia - Parâmetro [99] - Permitir gerar atendimento apenas para pacientes onde o Estágio autorização esteja como Autorizado */

ie_permite_atend_autorizado_w := obter_param_usuario(3130, 99, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_atend_autorizado_w);

if (ie_permite_atend_autorizado_w = 'S') and (ds_estagio_autor_p <> substr(obter_texto_tasy(60067, wheb_usuario_pck.get_nr_seq_idioma),1,255)) then
	ds_erro_p	:= substr(obter_texto_tasy(60068, wheb_usuario_pck.get_nr_seq_idioma),1,255);

elsif (coalesce(nr_atendimento_p::text, '') = '') then
	begin
	select	max(cd_pessoa_fisica),
		max(cd_medico_resp)
	into STRICT	cd_pessoa_fisica_p,
		cd_medico_resp_p
	from	paciente_setor
	where	nr_seq_paciente = nr_seq_paciente_p;
	end;
end if;

end;
$body$
