CREATE OR REPLACE PROCEDURE obter_dados_atrib_pat_bem ( nr_sequencia_p bigint, nr_seq_local_p INOUT bigint, cd_conta_p INOUT text, cd_centro_p INOUT bigint, tx_deprec_p INOUT bigint, nr_seq_regra_conta_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_local_w	bigint;
cd_conta_w	varchar(20);
cd_centro_w	bigint;
tx_deprec_w	bigint;
nr_seq_regra_conta_w bigint;



BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin

	select	nr_seq_local,
		cd_conta_contabil,
		cd_centro_custo,
		nr_seq_regra_conta
	into STRICT	nr_seq_local_w,
		cd_conta_w,
		cd_centro_w,
		nr_seq_regra_conta_w
	from	pat_bem
	where	nr_sequencia = nr_sequencia_p;



	tx_deprec_w := pat_obter_taxa_depreciacao(nr_sequencia_p);

	end;
end if;
nr_seq_local_p	:= nr_seq_local_w;
cd_conta_p	:= cd_conta_w;
cd_centro_p	:= cd_centro_w;
tx_deprec_p	:= tx_deprec_w;
nr_seq_regra_conta_p :=nr_seq_regra_conta_w;


commit;
end;
$body$
