CREATE OR REPLACE PROCEDURE obter_dados_disp_adep_js ( nr_prescricao_p INOUT bigint, nr_prescricoes_p text, qt_item_p bigint, cd_item_p text, cd_intervalo_p text, nm_usuario_p text, ie_retorno_p INOUT bigint, nr_seq_prescr_p INOUT bigint) AS $body$
DECLARE


--Criei como procedure para caso posteriormente seja necessÃ¡rio retornar mais de um valor
nr_prescricao_w		bigint := null;


BEGIN

ie_retorno_p	:= 0;
nr_seq_prescr_p := 0;

if (nr_prescricao_p > 0) then
	nr_prescricao_w := nr_prescricao_p;
else
	nr_prescricao_w := obter_max_nrprescricao(nr_prescricoes_p);
end if;

nr_prescricao_p := nr_prescricao_w;

if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then

	select  coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_prescr_p
	from    prescr_material
	where   nr_prescricao = nr_prescricao_w
	and     coalesce(qt_dose,0) = coalesce(qt_item_p,coalesce(qt_dose,0))
	and     cd_material = cd_item_p
	and     coalesce(cd_intervalo,'XPTO') = coalesce(cd_intervalo_p,'XPTO');

	if (nr_seq_prescr_p > 0) then

		select  count(a.nr_sequencia)
		into STRICT	ie_retorno_p
		from    ap_lote_item a,
			prescr_mat_hor b,
		 	prescr_material c
		where	c.nr_prescricao   = b.nr_prescricao
		and	c.nr_sequencia    = b.nr_seq_material
		and	b.nr_sequencia    = a.nr_seq_mat_hor
		and	c.nr_prescricao   = nr_prescricao_w
		and   	c.nr_sequencia	  = nr_seq_prescr_p;

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
