CREATE OR REPLACE PROCEDURE obter_dados_dose_unid_medida ( cd_material_p bigint, cd_unidade_medida_p INOUT text, qt_conversao_p INOUT bigint ) AS $body$
DECLARE



cd_unidade_medida_w	varchar(30);
qt_conversao_w		double precision;


BEGIN

select	max(cd_unidade_medida)
into STRICT	cd_unidade_medida_w
from 	unidade_medida_dose_v
where 	cd_material 		= cd_material_p
order by ie_prioridade;

select	max(qt_conversao)
into STRICT	qt_conversao_w
from 	material_conversao_unidade
where 	cd_material 		= cd_material_p
and 	cd_unidade_medida 	= cd_unidade_medida_p;

cd_unidade_medida_p 	:= cd_unidade_medida_w;
qt_conversao_p		:= qt_conversao_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
