CREATE OR REPLACE PROCEDURE obter_dados_email (nr_seq_contrato_p bigint, nm_usuario_p text, ds_email_retorno_p INOUT text) AS $body$
DECLARE

									 
 
qt_comunic_w		bigint;
nr_seq_regra_w		bigint;
ds_email_w			varchar(4000);
nr_seq_email_lib_w	bigint;
cd_pessoa_resp_w	bigint;
ds_texto_w			text;		
 

BEGIN 
 
select 	coalesce(count(*),0) 
into STRICT	qt_comunic_w 
from 	padrao_comunic_contrato 
where 	ie_situacao = 'A';
 
if (qt_comunic_w > 0) then 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_regra_w 
	from 	padrao_comunic_contrato 
	where 	ie_situacao = 'A';
	 
	nr_seq_email_lib_w := converte_rtf_html(' select ds_comunicado from padrao_comunic_contrato where nr_sequencia = :nr_seq ', nr_seq_regra_w, nm_usuario_p, nr_seq_email_lib_w);
	 
	select 	ds_texto ds_texto 
	into STRICT	ds_texto_w 
	from 	tasy_conversao_rtf 
	where 	nr_sequencia = nr_seq_email_lib_w;
 
	select 	cd_pessoa_resp cd_resp 
	into STRICT	cd_pessoa_resp_w 
	from 	contrato 
	where 	nr_sequencia = nr_seq_contrato_p;
	 
	if (cd_pessoa_resp_w <> '') then 
		select 	obter_compl_pf(cd_pessoa_resp_w,1,'M') 
		into STRICT	ds_email_w 
		;		
	end if;
	 
	if (ds_email_w <> '') then 
		ds_email_retorno_p := ds_email_w;
	end if;
	 
end if;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
