CREATE OR REPLACE PROCEDURE obter_dados_encaixe_cirurgia ( nr_atendimento_p bigint, nr_prescricao_p bigint, nr_cirurgia_p bigint, nm_usuario_p text, cd_pessoa_fisica_p INOUT text, cd_medico_p INOUT text, cd_convenio_p INOUT bigint) AS $body$
DECLARE

				 
cd_pessoa_fisica_w		varchar(10);
cd_medico_w			varchar(10);
cd_convenio_w			integer;
nr_atendimento_w		bigint;


BEGIN 
if (nr_atendimento_p > 0) then 
	begin 
	select	cd_pessoa_fisica, 
		cd_medico_resp, 
		obter_convenio_atendimento(nr_atendimento_p) 
	into STRICT	cd_pessoa_fisica_w, 
		cd_medico_w, 
		cd_convenio_w 
	from	atendimento_paciente 
	where	nr_atendimento	= nr_atendimento_p;
	exception 
		when others then 
		cd_pessoa_fisica_w	:= '';
		cd_medico_w		:= '';
		cd_convenio_w		:= 0;
	end;	
elsif (nr_prescricao_p > 0) then 
	begin 
	select	cd_pessoa_fisica, 
		cd_medico, 
		nr_atendimento 
	into STRICT	cd_pessoa_fisica_w, 
		cd_medico_w, 
		nr_atendimento_w 
	from	prescr_medica 
	where	nr_prescricao	= nr_prescricao_p;
	exception 
		when others then 
		cd_pessoa_fisica_w	:= '';
		cd_medico_w		:= '';
		nr_atendimento_w	:= 0;
	end;	
	 
	if (nr_atendimento_w > 0) then 
		cd_convenio_w	:= obter_convenio_atendimento(nr_atendimento_w);
	end if;
	 
elsif (nr_cirurgia_p > 0) then 
	begin 
	select	cd_pessoa_fisica, 
		substr(obter_medico_cirurgia(nr_cirurgia_p, 'C'),1,10), 
		coalesce(nr_atendimento,0), 
		coalesce(cd_convenio,0) 
	into STRICT	cd_pessoa_fisica_w, 
		cd_medico_w, 
		nr_atendimento_w, 
		cd_convenio_w 
	from	cirurgia 
	where	nr_cirurgia	= nr_cirurgia_p;
	exception 
		when others then 
		cd_pessoa_fisica_w	:= '';
		cd_medico_w		:= '';
		nr_atendimento_w	:= 0;
		cd_convenio_w		:= 0;
	end;	
	 
	if (cd_convenio_w	= 0) and (nr_atendimento_w > 0) then 
		cd_convenio_w	:= obter_convenio_atendimento(nr_atendimento_w);
	end if;
end if;
 
cd_pessoa_fisica_p	:= cd_pessoa_fisica_w;
cd_medico_p		:= cd_medico_w;
cd_convenio_p		:= cd_convenio_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
