SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_dados_end_pesssoa_js ( cd_logradouro_p text, ie_opcao_p text, nm_usuario_p text, ds_endereco_p INOUT text, ds_bairro_p INOUT text, ds_municipio_p INOUT text, sg_estado_p INOUT text) AS $body$
BEGIN

if (ie_opcao_p = 'N') then

	select 	max(nm_logradouro)
	into STRICT	ds_endereco_p
	from 	cep_logradouro
	where 	cd_logradouro = cd_logradouro_p;

	select 	max(nm_bairro)
	into STRICT	ds_bairro_p
	from 	cep_logradouro
	where 	cd_logradouro = cd_logradouro_p;

	select 	max(nm_localidade)
	into STRICT	ds_municipio_p
	from 	cep_logradouro
	where 	cd_logradouro = cd_logradouro_p;

	select 	max(cd_unidade_federacao)
	into STRICT	sg_estado_p
	from 	cep_logradouro
	where 	cd_logradouro = cd_logradouro_p;

else

	select 	max(nm_logradouro)
	into STRICT	ds_endereco_p
	from 	cep_log
	where 	cd_cep = cd_logradouro_p;

	select 	max(a.ds_bairro)
	into STRICT	ds_bairro_p
	from 	cep_bairro a,
		cep_log b
	where 	b.cd_bairro_inicial = a.nr_sequencia
	and 	b.cd_cep = cd_logradouro_p;

	select 	max(a.nm_localidade)
	into STRICT	ds_municipio_p
	from 	cep_loc a,
		cep_log b
	where 	a.nr_sequencia = b.nr_seq_loc
	and 	b.cd_cep = cd_logradouro_p;

	select 	max(ds_uf)
	into STRICT	sg_estado_p
	from 	cep_log
	where 	cd_cep = cd_logradouro_p;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_dados_end_pesssoa_js ( cd_logradouro_p text, ie_opcao_p text, nm_usuario_p text, ds_endereco_p INOUT text, ds_bairro_p INOUT text, ds_municipio_p INOUT text, sg_estado_p INOUT text) FROM PUBLIC;

