CREATE OR REPLACE PROCEDURE obter_dados_exames_relac_js ( nr_atendimento_p bigint, cd_material_p bigint, cd_pessoa_fisica_p INOUT text, dt_entrada_p INOUT timestamp, nm_pesssoa_fisica_p INOUT text, ds_material_p INOUT text, qt_dose_p INOUT bigint, cd_unidade_med_p INOUT text ) AS $body$
BEGIN
 
cd_pessoa_fisica_p 	:= '';
dt_entrada_p		:= null;
nm_pesssoa_fisica_p	:= '';
ds_material_p		:= '';
qt_dose_p		:= null;
cd_unidade_med_p	:= '';
 
select	cd_pessoa_fisica, 
	dt_entrada, 
    substr(obter_nome_pf(cd_pessoa_fisica),1,60) nm_pessoa_fisica 
into STRICT	cd_pessoa_fisica_p, 
	dt_entrada_p, 
	nm_pesssoa_fisica_p 
from  atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
select max(substr(obter_desc_material(cd_material),1,80)) ds_material, 
	max(qt_dose), 
	max(cd_unidade_medida_dose) cd_unidade_medida_dose 
into STRICT	ds_material_p, 
	qt_dose_p, 
	cd_unidade_med_p 
from  prescr_material 
where	cd_material = cd_material_p;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
