CREATE OR REPLACE PROCEDURE obter_dados_gerar_disp ( nr_seq_horario_p bigint, nr_prescricao_p bigint, nr_seq_item_p bigint, nr_etapa_sol_p bigint, ie_tipo_item_p text, qt_dispensar_hor_p INOUT bigint, nr_seq_kit_p INOUT bigint) AS $body$
DECLARE


qt_dispensar_hor_w	double precision;
nr_seq_kit_w		integer := 0;


BEGIN

if (ie_tipo_item_p in ('M', 'IAG', 'SNE')) then

	Select	coalesce(max(a.qt_dispensar_hor),0),
			coalesce(max(b.nr_seq_kit),0)
	into STRICT	qt_dispensar_hor_w,
			nr_seq_kit_w
	from	prescr_mat_hor a,
			prescr_material b
	where	a.nr_prescricao    = b.nr_prescricao
	and		a.nr_seq_material = b.nr_sequencia
	and		a.nr_sequencia = nr_seq_horario_p;

elsif (ie_tipo_item_p = 'SOL') then

	Select	coalesce(max(a.qt_dispensar_hor),0)
	into STRICT	qt_dispensar_hor_w
	from	prescr_mat_hor a,
		prescr_material b
	where	a.nr_prescricao   = b.nr_prescricao
	and	a.nr_seq_material = b.nr_sequencia
	and	a.nr_prescricao	  = nr_prescricao_p
	and	a.nr_seq_solucao  = nr_seq_item_p
	and	a.nr_etapa_sol	  = nr_etapa_sol_p;

end if;

qt_dispensar_hor_p := qt_dispensar_hor_w;
nr_seq_kit_p := nr_seq_kit_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
