CREATE OR REPLACE PROCEDURE obter_dados_gerar_planejamento ( nr_seq_tratamento_p bigint, nm_usuario_p text, dt_inicio_tratamento_p INOUT timestamp, qt_duracao_tratamento_p INOUT bigint, qt_meses_tratamento_p INOUT bigint, ie_tipo_trat_p INOUT text, ie_considera_trat_vol_p INOUT text, ie_horario_dif_p INOUT text) AS $body$
BEGIN

select	to_char(dt_inicio_trat,'dd/mm/yyyy'),
	qt_duracao_trat,
	months_between(trunc(dt_inicio_trat+qt_duracao_trat,'month'),trunc(dt_inicio_trat,'month'))+1,
	rxt_obter_tipo_trat_prot(nr_seq_protocolo),
	ie_horario_dif
into STRICT	dt_inicio_tratamento_p,
	qt_duracao_tratamento_p,
	qt_meses_tratamento_p,
	ie_tipo_trat_p,
	ie_horario_dif_p
from	rxt_tratamento
where	nr_sequencia = nr_seq_tratamento_p;

select	max(coalesce(ie_considera_tratamento_volume, 'N'))
into STRICT	ie_considera_trat_vol_p
from	rxt_parametro;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
