SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_dados_integracao (nr_seq_inf_integracao_p bigint, ie_opcao_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


/*

ie_opcao_p
1  - URL Webservice (DS_URL_WEBSERVICE)
2 - Action (DS_ACTION)
3 - FTP (DS_FTP)

*/
ds_url_webservice_w	varchar(255);
ds_action_w		varchar(255);
ds_ftp_w		varchar(255);


BEGIN

select 	max(ds_url_webservice),
	max(ds_action),
	max(ds_ftp)
into STRICT	ds_url_webservice_w,
	ds_action_w,
	ds_ftp_w
from 	cliente_integracao
where 	nr_seq_inf_integracao = nr_seq_inf_integracao_p
and 	ie_situacao = 'A';

if (ie_opcao_p = '1') then
	ds_retorno_p:= ds_url_webservice_w;
elsif (ie_opcao_p = '2') then
	ds_retorno_p:= ds_action_w;
elsif (ie_opcao_p = '3') then
	ds_retorno_p:= ds_ftp_w;
else
	ds_retorno_p:= '';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
