CREATE OR REPLACE PROCEDURE obter_dados_integra_sinapse_js ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, cd_empresa_p INOUT bigint, ds_pasta_exp_xml_p INOUT text, ds_nome_arquivo_p INOUT text, ie_integra_sinapse_p INOUT text) AS $body$
DECLARE


ie_incluir_acesso_dicom_w	varchar(1);	
nr_acesso_dicom_w		varchar(20) := '';				
					

BEGIN

select  coalesce(max(nr_seq_empresa_integr),0)
into STRICT	cd_empresa_p
from    empresa_integr_dados 
where  ((nr_seq_empresa_integr = 31) or (nr_seq_empresa_integr = 41) or (nr_seq_empresa_integr = 106) or (nr_seq_empresa_integr = 232))
and	coalesce(cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p;

select  max(ds_caminho_saida)
into STRICT	ds_pasta_exp_xml_p
from    empresa_integr_dados a,
        empresa_integracao b,
        sistema_integracao c
where   a.nr_seq_empresa_integr = b.nr_sequencia 
and	b.nr_sequencia = c.nr_seq_empresa
and	coalesce(a.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
and      b.nr_sequencia = cd_empresa_p;

select	coalesce(max(ie_incluir_acesso_dicom),'N')
into STRICT	ie_incluir_acesso_dicom_w
from   	empresa_integr_dados
where  	nr_seq_empresa_integr = cd_empresa_p
and    	coalesce(cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p;

if (ie_incluir_acesso_dicom_w = 'S') and (coalesce(nr_seq_prescricao_p,0) > 0) then
	
	select 	nr_acesso_dicom
	into STRICT	nr_acesso_dicom_w	
        from    prescr_procedimento
        where	nr_prescricao = nr_prescricao_p
        and	nr_sequencia = nr_seq_prescricao_p;
	
	nr_acesso_dicom_w := nr_acesso_dicom_w || '_';

end if;

if (nr_acesso_dicom_w <> '') then
	ds_nome_arquivo_p := obter_nm_arquivo_synapse(nr_prescricao_p);
	nr_acesso_dicom_w := '_' || nr_acesso_dicom_w || '.xml';
	ds_nome_arquivo_p := Substituir_Primeira_String(ds_nome_arquivo_p, '.xml', nr_acesso_dicom_w);
else
	ds_nome_arquivo_p := obter_nm_arquivo_synapse(nr_prescricao_p);
	
end if;

ie_integra_sinapse_p := verifica_se_integra_sinapse(nr_prescricao_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
