CREATE OR REPLACE PROCEDURE obter_dados_margem_adm ( nr_seq_horario_p bigint, ie_tipo_item_p text, dt_evento_med_p timestamp, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_margem_valida_p INOUT text, qt_minutos_margem_p INOUT bigint, ie_tipo_consistencia_p INOUT text, ie_acao_adm_fora_margem_p INOUT text) AS $body$
DECLARE


dt_horario_w				timestamp;
nr_prescricao_w				bigint;
qt_min_margem_adm_w			bigint;
qt_min_margem_adm_ant_w		bigint;
ie_margem_valida_w			varchar(5);
ie_tipo_consistencia_w		varchar(5);
ie_acao_adm_fora_margem_w	varchar(5);


BEGIN

qt_min_margem_adm_w := obter_param_usuario(88, 59, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, qt_min_margem_adm_w);
ie_acao_adm_fora_margem_w := obter_param_usuario(88, 58, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_acao_adm_fora_margem_w);
qt_min_margem_adm_ant_w := obter_param_usuario(1113, 507, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, qt_min_margem_adm_ant_w);

select	coalesce(max(nr_prescricao),0),
	max(dt_horario)
into STRICT	nr_prescricao_w,
	dt_horario_w
from	prescr_mat_hor
where	nr_sequencia = nr_seq_horario_p;

select	max(consistir_margem_administracao(nr_seq_horario_p, ie_tipo_item_p, dt_evento_med_p, qt_min_margem_adm_w, cd_perfil_p, 'S', qt_min_margem_adm_ant_w, nr_prescricao_w, dt_horario_w))
into STRICT	ie_margem_valida_w
;

select	max(consistir_margem_administracao(nr_seq_horario_p, ie_tipo_item_p, dt_evento_med_p, qt_min_margem_adm_w, cd_perfil_p, 'C', qt_min_margem_adm_ant_w, nr_prescricao_w, dt_horario_w))
into STRICT	ie_tipo_consistencia_w
;

ie_margem_valida_p 		:= ie_margem_valida_w;
ie_tipo_consistencia_p 		:= ie_tipo_consistencia_w;

if (ie_tipo_consistencia_p = 'A') then
	--Se tiver consistindo a margem anterior ao hor치rio de adm retorna o par칙metro 507
	qt_minutos_margem_p		:= qt_min_margem_adm_ant_w;
else
	--Se tiver consistindo a margem posterior ao hor치rio de adm retorna o par칙metro 59
	qt_minutos_margem_p		:= qt_min_margem_adm_w;
end if;

ie_acao_adm_fora_margem_p	:= ie_acao_adm_fora_margem_w;

end;
$body$
