CREATE OR REPLACE PROCEDURE obter_dados_nivel_cont_glic ( nr_seq_horario_p bigint, nr_seq_glicemia_p INOUT bigint, nr_seq_prot_glic_p INOUT bigint) AS $body$
DECLARE


nr_prescricao_w		bigint	:= 0;
nr_seq_glicemia_w	bigint;
nr_seq_prot_glic_w	bigint;


BEGIN

if (nr_seq_horario_p > 0) then
	begin

	select	a.nr_prescricao
	into STRICT	nr_prescricao_w
	from	prescr_mat_hor a
	where	a.nr_sequencia	= nr_seq_horario_p;

	if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
		begin

		select	coalesce(max(a.nr_sequencia),0)
		into STRICT	nr_seq_glicemia_w
		from	atend_glicemia a
		where	a.nr_prescricao	= nr_prescricao_w;

		select	coalesce(max(a.nr_seq_prot_glic),0)
		into STRICT	nr_seq_prot_glic_w
		from	atend_glicemia a
		where	a.nr_prescricao = nr_prescricao_w;

		end;
	end if;
	end;
end if;

nr_seq_glicemia_p	:= nr_seq_glicemia_w;
nr_seq_prot_glic_p	:= nr_seq_prot_glic_w;

end;
$body$
