CREATE OR REPLACE PROCEDURE obter_dados_nova_prescricao ( nr_sequencia_p bigint, qt_dt_prescr_anteced_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, qt_peso_p INOUT bigint, qt_altura_p INOUT bigint, dt_prescricao_p INOUT timestamp, nr_atendimento_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_peso_w		real;
qt_altura_w		real;
nr_atendimento_w	bigint;
dt_prescricao_w		timestamp;

BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	begin 
	qt_peso_w	:= obter_sinal_vital(nr_atendimento_p,'Peso');
	qt_altura_w	:= obter_sinal_vital(nr_atendimento_p,'Altura');
	 
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
		begin 
		if (qt_dt_prescr_anteced_p = 0) then 
			begin 
			select	coalesce(max(dt_cirurgia), clock_timestamp()) 
			into STRICT	dt_prescricao_w 
			from	aval_pre_anestesica 
			where	nr_sequencia	= nr_sequencia_p;
			end;
		else 
			begin 
			select	coalesce(max(dt_cirurgia), clock_timestamp()) - qt_dt_prescr_anteced_p/1440 
			into STRICT	dt_prescricao_w 
			from	aval_pre_anestesica 
			where	nr_sequencia	= nr_sequencia_p;
			end;
		end if;
		end;
	end if;	
	 
	select	max(nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	cd_estabelecimento = cd_estabelecimento_p 
	and	ie_tipo_atendimento = 1 
	and	coalesce(dt_alta::text, '') = '' 
	and	ie_fim_conta <> 'F';	
	end;
end if;
commit;
qt_peso_p		:= qt_peso_w;
qt_altura_p		:= qt_altura_w;
dt_prescricao_p		:= dt_prescricao_w;
nr_atendimento_p	:= nr_atendimento_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
