CREATE OR REPLACE PROCEDURE obter_dados_pes_checkup_js ( cd_pessoa_fisica_p text, cd_convenio_p bigint, ie_existe_p INOUT text, cd_empresa_p INOUT bigint, qt_checkup_p INOUT bigint ) AS $body$
BEGIN

ie_existe_p := '';
qt_checkup_p := 0;

if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '' AND cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	        select  count(*)
		into STRICT qt_checkup_p
		from    empresa_contrato_checkup a,
			empresa_pessoa_checkup b
		where   b.nr_seq_contrato  = a.nr_sequencia
		and     a.cd_convenio      = cd_convenio_p
		and     b.cd_pessoa_fisica = cd_pessoa_fisica_p;
end if;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

		select  max(cd_pessoa_fisica)
		into STRICT	ie_existe_p
		from 	empresa_pessoa_checkup
		where   cd_pessoa_fisica = cd_pessoa_fisica_p;
end if;

select  max(coalesce(a.cd_empresa,0))
into STRICT	cd_empresa_p
from    empresa_contrato_checkup a,
        empresa_pessoa_checkup b
where   a.nr_sequencia = b.nr_seq_contrato
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p;


end;
