CREATE OR REPLACE PROCEDURE obter_dados_prim_checagem ( cd_material_p bigint, nr_seq_horario_p bigint, ie_prim_checagem_p INOUT text, ie_prim_check_valido_p INOUT text, ds_texto_p INOUT text) AS $body$
DECLARE


ie_prim_checagem_w	varchar(1);
ie_prim_check_valido_w	varchar(1);
ds_texto_w		varchar(255);


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	SELECT	coalesce(MAX(ie_dupla_checagem),'N') ie_prim_checagem
	into STRICT	ie_prim_checagem_w
	FROM	material
	WHERE	cd_material = cd_material_p;

	ie_prim_checagem_p	:= ie_prim_checagem_w;
end if;

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then
	select  coalesce(max('S'),'N')
	into STRICT	ie_prim_check_valido_w
	from    prescr_mat_hor
	where   nr_sequencia = nr_seq_horario_p
	and     coalesce(dt_primeira_checagem::text, '') = ''
	and     coalesce(dt_fim_horario::text, '') = ''
	and     coalesce(dt_suspensao::text, '') = '';

	ie_prim_check_valido_p	:= ie_prim_check_valido_w;
end if;

select	obter_texto_tasy(135872, 1)
into STRICT	ds_texto_w
;

ds_texto_p := ds_texto_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
