CREATE OR REPLACE PROCEDURE obter_dados_reg_entrega_laudo (nr_atendimento_p bigint, cd_retirador_p text, nm_retirador_p text, ds_observacao_p text, dt_entrega_p timestamp, ie_mostra_debito_p text, ie_possui_registros_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ds_mensagem_erro_p INOUT text) AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(15);
ie_mostra_w		varchar(1);
ie_opcao_alerta_w	varchar(1);
ds_mensagem_erro_w	varchar(255);
ds_mensagem_w		varchar(255);
cd_pessoa_cominic_w	varchar(10);
cd_perfil_comunic_w	bigint;
				

BEGIN 
 
select 	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from 	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
ie_mostra_w := obter_se_mostra_debito_estab(cd_pessoa_fisica_w, cd_estabelecimento_p);
 
if (ie_mostra_debito_p = 'S') and (ie_mostra_w = 'S') then 
	SELECT * FROM consiste_paciente_debito(cd_estabelecimento_p, cd_pessoa_fisica_w, 'N', nm_usuario_p, ie_opcao_alerta_w, ds_mensagem_w, cd_perfil_comunic_w, cd_pessoa_cominic_w, nr_atendimento_p) INTO STRICT ie_opcao_alerta_w, ds_mensagem_w, cd_perfil_comunic_w, cd_pessoa_cominic_w;
	--verfiicar a passagem do par√¢metros nr_atendimento_p 
	 
	if	((ie_opcao_alerta_w = 'S') or (ie_opcao_alerta_w = 'R') or (ie_opcao_alerta_w = 'J')) then 
		ds_mensagem_erro_w := Obter_Desc_Expressao(654061);
	end if;
end if;
 
if (ie_possui_registros_p = 'S' and (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '')) then 
	CALL registrar_entrega_exame_barra(nr_atendimento_p, cd_retirador_p, nm_retirador_p, ds_observacao_p, dt_entrega_p, nm_usuario_p);
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
