CREATE OR REPLACE PROCEDURE obter_dados_san_reserva_hemot ( nr_seq_agenda_p bigint, nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, cd_pessoa_fisica_p INOUT text, nr_atendimento_out_p INOUT bigint, cd_medico_p INOUT text, cd_convenio_p INOUT bigint, cd_procedimento_p INOUT bigint, ie_origem_proced_p INOUT bigint, nr_seq_entidade_p INOUT bigint, cd_unidade_externa_p INOUT text, dt_cirurgia_p INOUT timestamp) AS $body$
DECLARE


copiar_data_agenda_w	varchar(1);


BEGIN

if (nr_seq_agenda_p > 0) then
	begin
	SELECT * FROM gerar_reserva_hemo_agenda(
		nr_seq_agenda_p, cd_pessoa_fisica_p, nr_atendimento_out_p, cd_medico_p, cd_convenio_p, cd_procedimento_p, ie_origem_proced_p) INTO STRICT cd_pessoa_fisica_p, nr_atendimento_out_p, cd_medico_p, cd_convenio_p, cd_procedimento_p, ie_origem_proced_p;
	end;
end if;

copiar_data_agenda_w := obter_param_usuario(450, 365, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, copiar_data_agenda_w);
if (copiar_data_agenda_w = 'S') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nr_seq_agenda_p > 0) then
	begin
	select	coalesce(max(hr_inicio), clock_timestamp())
	into STRICT	dt_cirurgia_p
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;
	end;
end if;

select 	max(nr_seq_entidade)
into STRICT	nr_seq_entidade_p
from  	san_parametro
where  	cd_estabelecimento = cd_estabelecimento_p;

select	max(cd_unidade_externa)
into STRICT	cd_unidade_externa_p
from	atend_paciente_unidade
where	nr_atendimento = nr_atendimento_p;

select	CASE WHEN coalesce(max(a.nr_sequencia),0)=0 THEN  nr_seq_entidade_p  ELSE coalesce(max(a.nr_sequencia),0) END
into STRICT	nr_seq_entidade_p
from	san_entidade a
where	a.cd_setor_atendimento =
	(SELECT	max(b.cd_setor_atendimento)
	from	atend_paciente_unidade b
	where	b.nr_seq_interno =
		(select	max(c.nr_seq_interno)
		from	atend_paciente_unidade c
		where	c.nr_atendimento = nr_atendimento_p));

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
