CREATE OR REPLACE PROCEDURE obter_dados_se_contr_duplicado ( nr_seq_controle_p bigint, nr_sequencia_p bigint, nr_seq_visita_p INOUT bigint, nr_atendimento_p INOUT bigint, nm_visitante_p INOUT text, ds_msg_p INOUT text, ds_msg_compl_p INOUT text) AS $body$
DECLARE

 
nr_atendimento_w	bigint;
nm_visitante_w		varchar(60);


BEGIN 
 
if (nr_seq_controle_p IS NOT NULL AND nr_seq_controle_p::text <> '' AND nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	begin 
	select	max(nr_sequencia), 
		max(nr_atendimento), 
		max(substr(coalesce(obter_nome_pf(cd_pessoa_fisica), nm_visitante), 1, 60)) 
	into STRICT	nr_seq_visita_p, 
		nr_atendimento_w, 
		nm_visitante_w 
	from	atendimento_visita 
	where	nr_sequencia = 
		(SELECT	max(nr_sequencia) 
		from	atendimento_visita 
		where	nr_seq_controle	= nr_seq_controle_p 
		and	coalesce(dt_saida::text, '') = '' 
		and	nr_sequencia	<> nr_sequencia_p);
 
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
		if (nm_visitante_w IS NOT NULL AND nm_visitante_w::text <> '') then 
			ds_msg_p	:= substr(obter_texto_tasy(41639, wheb_usuario_pck.get_nr_seq_idioma),1,300);
		else 
			ds_msg_p	:= substr(obter_texto_tasy(41640, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		end if;
	elsif (nm_visitante_w IS NOT NULL AND nm_visitante_w::text <> '') then 
		ds_msg_p	:= substr(obter_texto_tasy(41641, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	else 
		ds_msg_p	:= substr(obter_texto_tasy(41642, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end if;
 
	ds_msg_compl_p	:= substr(obter_texto_tasy(41643, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	nr_atendimento_p	:= nr_atendimento_w;
	nm_visitante_p	:= nm_visitante_w;
	end;
end if;
 
end;
$body$
